import { useParams } from "react-router-dom";
import useFetch from "./useFetch";

const UserDetails = () => {
  const { id } = useParams();
  const { data: user, error, isPending } = useFetch('http://localhost:8000/usedetails/' + id);
  // const history = useHistory();

  const handleClick = () => {
    fetch('http://localhost:8000/users/' + user.id, {
      method: 'DELETE'
    }).then(() => {
      // history.push('/');
    }) 
  }

  return (
    <div className="blog-details">
      { isPending && <div>Loading...</div> }
      { error && <div>{ error }</div> }
      { user && (
        <article>
          <h2>{ user.username }</h2>
          <p>Written by { user.address }</p>
          <div>{ user.gender }</div>
          <button onClick={handleClick}>delete</button>
        </article>
      )}
    </div>
  );
}
 
export default UserDetails;
