import { useState, useEffect } from "react";
import UserList from "./UserList";


const Home = () => {
    const [users, setUsers] = useState([
        { username: 'vishnu', address: 'lorem ipsum...', gender: 'Male', id: 1 },
        { username: 'priya', address: 'lorem ipsum...', gender: 'Female', id: 2 },
        { username: 'siya', address: 'lorem ipsum...', gender: 'Female', id: 3 }
    ])


    const deleteUser = (id) => {
        const newUsers = users.filter(user => user.id !== id);
        setUsers(newUsers);
    }

    useEffect(() => {
        console.log("use effect ran")
        console.log("use effect ran")
    })

    return (
    <div className="home">
        <UserList users={users} title="All Users!" deleteUser={deleteUser} />
        {/* <UserList users={users.filter((user) => user.username === 'vishnu')} title="Admin User" /> */}
    </div>  
  );
}
 
export default Home;
