import { useParams } from "react-router-dom";
import useFetch from "./usefetch";
import { useState } from "react";


const UserDetails  = () => {

    const {id} = useParams();
    const {data: user, isPending, error} = useFetch('http://localhost:8000/users/'+id)

    const [username, setUserName] = useState('');
    const [address, setAddress] = useState('');
    const [gender, setGender] = useState('');
    // const history = useHistory();



    const userEdit = (id,username,address,) => {
      alert(id)
      setUserName(username)
      setAddress(address)
      setGender(gender)

      // e.preventDefault();
      const user = {id,username,address,gender}
      fetch('http://localhost:8000/users/'+id, {
          method: 'PUT',
          headers: { "Content-Type": "application/json"},
          body: JSON.stringify(user)
      }).then(()=> {
          console.log("update user",user)
          })
      // history.go('-1');
  }

    return ( 
       <div className="blog-details">
        <h2>{ user && user.username }'s Profile</h2>        
        {isPending && <div>Loading...</div>}
        {error && <div>{error}</div>}
        {user && (
          <div>
              <p>Adress: { user.address }</p>
              <p>Gender: { user.gender }</p>
              
          </div>          
        )}

      <button onClick={() => userEdit(user.id,user.username,user.address,user.gender)}> Edit</button>  



      <form onSubmit={userEdit}>
            <label>UserName:</label>
            <input 
                type="text" 
                required value={username}
                onChange={(e) => setUserName(e.target.value)}
            />

            <label>Address:</label>
            <textarea 
                required value={address}
                onChange={(e) => setAddress(e.target.value)}> 
            </textarea>

            <label>Gender</label>
            <select
                value={gender}
                onChange={(e)=> setGender(e.target.value)}>
                <option value="Female">Female</option>
                <option value="Male">Male</option>
            </select>

            <button>Update</button>
        </form>  
       </div> 



     );
}
 
export default UserDetails;
